$('input[name=es_paciente]').click(function(){
    var seleccionado = $(this).val();
    documento_paciente = document.getElementById('documento_paciente').value.replaceAll(" ",'').trim();
    nombre_paciente = document.getElementById('nombre_completo_paciente').value.trim().replaceAll(" ",' ').toUpperCase();
    celular_paciente = document.getElementById('celular_paciente').value.replaceAll(" ",'').trim();
    if(seleccionado==="Si")
    {
        if(documento_paciente!="" && documento_paciente!="" && celular_paciente!="")
        {
            $('#documento_solicitante').val(documento_paciente).attr('disabled','').attr('readonly','');
            $('#nombre_completo_solicitante').val(nombre_paciente).attr('disabled','').attr('readonly','');
            $('#celular_solicitante').val(celular_paciente).attr('disabled','').attr('readonly','');
        }
        else
        {
            $('#staticBackdrop').modal('show');
            $('#informacion').attr('class','modal-body text-start').html('Los siguientes campos del paciente son obligatorios:<br><li>Tipo de documento</li><li>Número de identificación</li><li>Nombre Completo</li><li>Celular</li>');
        }
    }
    else if(seleccionado==="No")
    {
        $('#documento_solicitante').val("").removeAttr('disabled','').removeAttr('readonly','');
        $('#nombre_completo_solicitante').val("").removeAttr('disabled','').removeAttr('readonly','');
        $('#celular_solicitante').val("").removeAttr('disabled','').removeAttr('readonly','');
    }
});
