$('#enviar').click(function() {
    /* PACIENTE */
    // var tip_doc_paciente = $('#tipo_documento_paciente')
    var doc_paciente = $('#documento_paciente').val().trim();
    var nom_paciente = $('#nombre_completo_paciente').val().trim();
    var cel_paciente = $('#celular_paciente').val().trim();
    /* SOLICITANTE */
    // var tip_doc_solicitante = $('#tipo_documento_solicitante')
    var doc_solicitante = $('#documento_solicitante').val().trim();
    var nom_solicitante = $('#nombre_completo_solicitante').val().trim();
    var cel_solicitante = $('#celular_solicitante').val().trim();
    /* SOLICITANTE */
    // var tip_solicitud = $('#tipo_solicitud')
    // var ips = $('#ips')
    var mensaje = $('#mensaje').val().trim();
    if(doc_paciente!="" && nom_paciente!="" && cel_paciente!="" && doc_solicitante!="" && nom_solicitante!="" && cel_solicitante!="" && mensaje!="")
    {
        $('#staticBackdrop').modal('show');
    }
});

